mapper.select:
select count(*) from SIMCO_ANALISIS_TEMA_ACTOR
where N_ID_CASO =#{caso.id}
and N_ID_ACTOR = #{actor.id}

select A.*, B.C_DETALLE from SIMCO_ANALISIS_TEMA_ACTOR A
INNER JOIN SIMCO_ANALISIS_TEMA B ON A.N_ID_TEMA = B.N_ID_TEMA
where A.N_ID_CASO = #{id}

select count(*) from SIMCO_ANALISIS_TEMA_ACTOR
where N_ID_CASO =#{caso.id}
and N_ID_ACTOR = #{actor.id}
and N_ID_TEMA = #{tema.id}

mapper.delete:
delete from SIMCO_ANALISIS_TEMA_ACTOR
where N_ID_CASO =#{caso.id}
and N_ID_ACTOR = #{actor.id}
and N_ID_TEMA = #{tema.id}

delete from SIMCO_ANALISIS_TEMA_ACTOR
where N_ID_CASO =#{caso.id}
and N_ID_ACTOR = #{actor.id}
