mapper.sql:
SELECT A.N_ID_ACTIVIDAD, A.C_NOMACTIVIDAD, A.C_DESCRIPACTIVIDAD, A.C_LUGARREALIZACION, A.D_FECHAREALIZACIONINI, A.D_FECHAREALIZACIONFIN, A.T_HORAREALIZACIONINI, A.T_HORAREALIZACIONFIN, A.C_CODIGOACTIVIDAD, A.N_INDCURSO, A.C_COMENTARIOACTIVIDAD, B.VALOR_PARAMETRO AS PARAM_TIPO_ACTIVIDAD_VAL, B.NOMBRE_PARAMETRO AS PARAM_TIPO_ACTIVIDAD_NOM, A.C_TIPOACONTECIMIENTO, A.C_TIPOVIOLENCIA, A.C_RESULTADOVIOLENCIA, A.N_ID_ACONTECIMIENTO, A.N_INDICEVINCULADO, A.N_INDICEACTIVO FROM SIMCO_REG_ACTIVIDAD A LEFT JOIN SIMCO_PARAMETRO B ON A.C_TIPO_ACTIVIDAD = B.VALOR_PARAMETRO AND B.PADRE_PARAMETRO = 10 AND A.N_INDICEACTIVO = 1
A.C_NOMACTIVIDAD=#{nombre}
A.N_ID_ACTIVIDAD=#{id}
ORDER BY A.N_ID_ACTIVIDAD DESC
