mapper.select:
SELECT COUNT(*) TOTAL_MES FROM SIMCO_REG_CASO WHERE D_FECHAINICIO IS NOT NULL AND C_INDVIGENTE = 'A' AND C_INDAPROBADO = 'A'
AND TO_CHAR(D_FECHAINICIO, 'YY') = #{anhos}
AND TO_CHAR(D_FECHAINICIO, 'MM') = #{mes}

SELECT COUNT(*) TOTAL_MES FROM SIMCO_REG_CASO WHERE D_FECHAINICIO IS NOT NULL AND C_INDVIGENTE = 'A' AND C_INDAPROBADO = 'A'
AND TO_CHAR(D_FECHAMODIFICACION, 'YY') = #{anhos}
AND TO_CHAR(D_FECHAMODIFICACION, 'MM') = #{mes}
AND C_TIPOESTADO IN ('06')

        SELECT COUNT(DISTINCT C.N_ID_ACTIVIDAD) FROM SIMCO_REG_CASO A 
INNER JOIN SIMCO_REG_CASO_ACTIVIDAD B ON A.N_ID_CASO = B.N_ID_CASO
INNER JOIN SIMCO_REG_ACTIVIDAD C ON B.N_ID_ACTIVIDAD = C.N_ID_ACTIVIDAD AND C.C_TIPO = 'AC' AND C.N_INDICEACTIVO = 1
WHERE A.C_INDVIGENTE = 'A' AND A.C_INDAPROBADO = 'A' AND C.C_TIPOACONTECIMIENTO IN ('01','02','03','04','05','06','07','08')
AND TO_CHAR(C.D_FECMODIFICACION, 'YY') = #{anhos}
AND TO_CHAR(C.D_FECMODIFICACION, 'MM') = #{mes}

        SELECT COUNT(DISTINCT A.N_ID_CASO) FROM SIMCO_REG_CASO A 
INNER JOIN SIMCO_REG_CASO_ACTIVIDAD B ON A.N_ID_CASO = B.N_ID_CASO
INNER JOIN SIMCO_REG_ACTIVIDAD C ON B.N_ID_ACTIVIDAD = C.N_ID_ACTIVIDAD AND C.C_TIPO = 'AD' AND C.N_INDICEACTIVO = 1
WHERE A.C_INDVIGENTE = 'A' AND A.C_INDAPROBADO = 'A' 
AND TO_CHAR(C.D_FECMODIFICACION, 'YY') = #{anhos}
AND TO_CHAR(C.D_FECMODIFICACION, 'MM') = #{mes}

SELECT COUNT(*) TOTAL_ACTIVO FROM SIMCO_REG_CASO WHERE D_FECHAPUBLICACION IS NOT NULL AND C_INDVIGENTE = 'A'
AND C_TIPOESTADO = '06'
AND TO_CHAR(D_FECHAINICIO, 'YY') = #{anhos}
AND TO_CHAR(D_FECHAINICIO, 'MM') = #{mes}

SELECT COUNT(*) TOTAL_ACTIVO FROM SIMCO_REG_CASO WHERE C_INDVIGENTE = 'A' AND C_INDAPROBADO = 'A'
AND C_TIPOCASO = '09'
AND TO_CHAR(D_FECHAINICIO, 'YY') = #{anhos}
AND TO_CHAR(D_FECHAINICIO, 'MM') = #{mes}
