mapper.sql:
SELECT A.N_ID_ACTOR, A.C_NOMACTOR, A.C_APELLIDOPATACTOR, A.C_APELLIDOMATACTOR, A.C_DNIACTOR, A.C_RUCACTOR, A.D_FECNACIMIENTOACTOR, A.C_LUGNACIMIENTOACTOR, A.C_INFOCONTACTO, A.C_SEXO, A.C_DIRECCIONACTOR, A.C_TELEFONOACTOR, A.C_EMAILACTOR, A.C_ANEXOACTOR, A.C_CARGOACTOR, A.C_TIPOGENERAL, A.C_EMPRESATIPO, A.C_EMPRESAAMBITO, A.C_OBSERVACIONES, A.C_ENTIDADTIPO, A.C_IDDEPARTAMENTO, A.C_IDPROVINCIA, A.C_IDDISTRITO, A.C_TIPOPOBLACION, A.C_SUBTIPO1POBLACION, A.C_SUBTIPO2POBLACION, A.C_TIPOESTADO, A.C_SUBTIPO1ESTADO, A.C_SUBTIPO2ESTADO, A.C_TIPOEMPRESA, A.C_SUBTIPO1EMPRESA, A.C_SUBTIPO2EMPRESA, A.C_TIPOORGANIZACION, A.C_SUBTIPO1ORGANIZACION, A.C_SUBTIPO2ORGANIZACION, A.C_SUBTIPO3EMPRESA, A.C_CLASIFICACION FROM SIMCO_REG_ACTOR A
UPPER(A.C_NOMACTOR||' '||A.C_APELLIDOPATACTOR||' '||A.C_APELLIDOMATACTOR) LIKE '%'||UPPER(#{nombre})||'%'
A.C_DNIACTOR=#{dni}
A.N_ID_ACTOR=#{id}
ORDER BY A.N_ID_ACTOR

